def check_size(length, side, area):
    other = 1 - side
    if length[other] == 0:
        if area > 0:
            return float('-inf')  # Return negative infinity if we cannot satisfy the area requirement.
        return 0  # Area is zero and length is enough.

    # Calculate the required length to satisfy the area using division.
    need = (area + length[other] - 1) // length[other]

    # Return the remaining length for this side after subtracting needed length.
    return length[side] - need


def rec(length, rem):
    for i in range(3):
        if not rem & (1 << i):
            continue

        n_rem = rem ^ (1 << i)

        for side in range(2):
            nxt_length = check_size(length, side, area[i])

            if nxt_length < 0:  # If we can't satisfy this area, skip
                continue

            if nxt_length == 0 and n_rem == 0:  # All areas have been matched
                return 1

            if nxt_length >= 0:
                pre_length = length[side]
                length[side] = nxt_length

                if rec(length, n_rem):
                    return 1

                length[side] = pre_length

    return 0


