class Motion:
    def __init__(self) -> None:
        self.dx = 1  # Initial direction: right
        self.dy = 0
        self.x = 0
        self.y = 0
        self.directions = [(1, 0), (0, -1), (-1, 0), (0, 1)]  # Right, Down, Left, Up
        self.direction_index = 0  # Start facing 'Right'

    def rotate(self) -> None:
        self.direction_index = (self.direction_index + 1) % 4  # Rotate right
        self.dx, self.dy = self.directions[self.direction_index]  # Update direction based on index

    def move(self) -> None:
        self.x += self.dx
        self.y += self.dy

    @property
    def get_x(self) -> int:
        return self.x

    @property
    def get_y(self) -> int:
        return self.y

