def judge1():
    N = int(input())
    ST = [input().split() for _ in range(N)]

    for i in range(N):
        si, ti = ST[i]
        found_si = found_ti = False

        for j in range(N):
            if i == j:
                continue
            if si in ST[j]:
                found_si = True
            if ti in ST[j]:
                found_ti = True

        # We return False only if both found_si and found_ti are True.
        if found_si and found_ti:
            return False

    return True

