def rec(n, memo=None):
    if memo is None:
        memo = {}

    if n == 0:
        return '1'
    if n in memo:
        return memo[n]

    memo[n] = str(int(rec(n // 2, memo)) + int(rec(n // 3, memo)))

    return memo[n]

