def subsolve1(n):
    a = [0]*n
    for i in range(n):
        print(f'? {i+1}')
        a[i] = iinput()
    return a  # Instead of printing and exiting, we return the result

def subsolve2(n, k):
    a = [0]*n
    b = [0]*(k+1)
    for i in range(k+1):
        print('?', end='')
        for j in range(k+1):
            if j == i:
                continue
            print(f' {j+1}', end='')
        print()
        b[i] = iinput()
    c = sum(b) % 2
    for i in range(k+1):
        a[i] = (c - b[i]) % 2
    c = sum(a[:k-1]) % 2
    for i in range(k+1, n):
        print('?', end='')
        for j in range(k-1):
            print(f' {j+1}', end='')
        print(f' {i+1}')
        t = iinput()
        a[i] = (t - c) % 2
    return a  # Return the result instead of printing and exiting

def solve(args):
    n, k = args
    if k == 1:
        return subsolve1(n)  # Return the result from subsolve1
    else:
        return subsolve2(n, k)  # Return the result from subsolve2

