def perm(n):
    rt = []
    def iter(s, acc):
        if not s:
            rt.append(acc[:])
        for i in range(len(s)):
            v = s[i]
            s.remove(v)
            acc.append(v)
            iter(s, acc)
            acc.pop()
            s.insert(i, v)
    iter(list(range(n)), [])
    return rt

def tp(tbl):
    H = len(tbl)
    W = len(tbl[0])
    t = [['' for _ in range(H)] for _ in range(W)]
    for i in range(H):
        for j in range(W):
            t[j][i] = tbl[i][j]
    return t

def f(s):
    n = 0
    for i in range(len(s)):
        for j in range(i):
            if s[j] > s[i]:
                n += 1
    return n

