def calc(B, x, y):
    n = len(B)
    next_x = [n] * n
    next_y = [n] * n

    # Fill the next_x and next_y arrays
    for i in range(n - 1, -1, -1):
        if B[i] == x:
            next_x[i] = i
        else:
            next_x[i] = next_x[i + 1] if i + 1 < n else n

        if B[i] == y:
            next_y[i] = i
        else:
            next_y[i] = next_y[i + 1] if i + 1 < n else n

    r = 0
    for i in range(n):
        k = max(next_x[i], next_y[i])
        if k < n:
            r += (n - k)
    return r

