def rotate(piece, n):
    if n == 0:
        return piece
    if n == 1:
        return [list(reversed(col)) for col in zip(*piece)]
    if n == 2:
        return [row[::-1] for row in piece[::-1]]
    if n == 3:
        return [list(col) for col in zip(*piece)][::-1]


R = []
for p in P:
    R.append([rotate(p, i) for i in range(4)])


def slide(p, n):
    rotated = R[p][n // 49]
    row = (n % 49) // 7 - 3
    col = (n % 49) % 7 - 3
    result = [[0] * 4 for _ in range(4)]

    for i in range(4):
        for j in range(4):
            if rotated[i][j]:
                if 0 <= i + row < 4 and 0 <= j + col < 4:
                    result[i + row][j + col] = 1
                else:
                    return None
    return result


