            return "No"
        N[i] = l

    RN = list(zip(*reversed(N)))
    for l in RN:
        if len(set(l)) < 9 or any(x < 1 or x > 9 for x in l):
            return "No"

    for i in range(3):
        for j in range(3):
            a = set()
            b = set()
            c = set()
            for k in range(3):
                a |= set(N[3*i+k][:3])
                b |= set(N[3*i+k][3:6])
                c |= set(N[3*i+k][6:])
            if len(a) < 9 or len(b) < 9 or len(c) < 9 or any(x < 1 or x > 9 for x in a | b | c):
                return "No"

    return "Yes"

