def rec(length, rem):
    for i in range(3):
        if not rem & 1 << i:
            continue

        n_rem = rem ^ 1 << i

        for side in range(2):
            nxt_length = check_size(length, side, area[i])

            if nxt_length < 0:
                continue  # skip if there's not enough length

            # If there is no length left for the side but still areas remaining
            if nxt_length == 0 and n_rem:
