def check_size(length, side, area):
    other = 1 - side
    # Calculate the minimum length required to satisfy the area for current side
    if length[other] == 0:
        return float('-inf')  # Return negative infinity if other length is 0 to avoid division by zero
    need = (area + length[other] - 1) // length[other]

    return length[side] - need


def rec(length, rem):
    for i in range(3):
        if not (rem & (1 << i)):
            continue

        n_rem = rem ^ (1 << i)

        for side in range(2):
            nxt_length = check_size(length, side, area[i])

            if nxt_length < 0:
                continue  # Skip if not enough length to satisfy

            if nxt_length == 0 and n_rem == 0:
                return 1  # Successfully used all areas

            pre_length = length[side]
            length[side] = nxt_length

            if rec(length, n_rem):
                return 1

            # Backtrack to original length
            length[side] = pre_length

    return 0


