def ans(n, k, bit):
    b = 1 << bit
    if bit < 0:
        return k  # If bit is negative, return k directly

    if (n & b) == 0:
        return ans(n, k, bit - 1)

    if n == b:
        return k - 1 if k % 2 == 0 else k + 1
    elif n == b + 1:
        if k == 1:
            return 2
        elif k == n - 1:
            return n
        elif k % 2 == 1:
            return k - 2
        else:
            return k + 2
    else:
        if k <= n - b:
            return ans(n - b, k, bit - 1)
        elif k >= b + 1:
            return ans(n - b, k - b, bit - 1) + b  # Correcting the adjustment on k
        else:
            return k

