memo = {}

def calc(l, r, tl, tr, flag, v):
    if l > r:
        return 0
    if l == r:
        if (l * tl) ^ (r * tr) == v and flag:
            return 1
        return 0

    if (l, r, tl, tr, flag, v) in memo:
        return memo[(l, r, tl, tr, flag, v)]

    res = 0
    for l0 in range(2):
        for r0 in range(2):
            sl = l0 & tl
            sr = r0 & tr
            if sl ^ sr == (v & 1):
                nflag = 0
                if l0 != r0:
                    nflag = (l0 < r0)
                else:
                    nflag = flag
                res += calc((l + 1 - l0) // 2, (r - r0) // 2, tl, tr, nflag, v >> 1)

    res %= mod
    memo[(l, r, tl, tr, flag, v)] = res
    return res

