def solve(N, K):
    def rev(x):
        return int(''.join(reversed(list(str(x)))))

    def cnt(x):
        res = 0
        current = x  # Start with x
        while current <= N:
            res += 1
            current *= 10  # Move to the next power of 10 times x

        return res

    if K % 10 == 0:
        return 0

    if K > rev(K):
        return 0

    # Count distinct instances for K and rev(K)
    k_count = cnt(K)
    rev_k_count = cnt(rev(K))

    # Since K and rev(K) may count the same if K equals rev(K),
    # we need to ensure we don't double count
    if K == rev(K):
        return k_count  # Just return one of the counts

    return k_count + rev_k_count

