def f(t, l):
    if t < 0:
        return 0  # no more bits to check, return 0
    nl1 = []
    nl2 = []
    for li in l:
        if (li >> t) & 1:  # Correct precedence with parentheses
            nl1.append(li)
        else:
            nl2.append(li)
    if nl1 and nl2:
        return min(f(t - 1, nl1), f(t - 1, nl2)) | (1 << t)
    elif nl1:
        return f(t - 1, nl1)
    elif nl2:
        return f(t - 1, nl2)
    else:
        return 0  # If both are empty

