def dfs(digit,A,res):
  S = []
  T = []
  for a in A:
    if (a >> digit) & 1:
      S.append(a)
    else:
      T.append(a)

  if digit == 0:
    global ans
    if len(S) == 0 or len(T) == 0:
      ans = min(ans,res)
    else:
      ans = min(ans,res + 1)
    return

  if len(S) == 0:
    dfs(digit-1,T,res)
  elif len(T) == 0:
    dfs(digit-1,S,res)
  else:
    # Change made here: making only one call for the distinct paths
    dfs(digit-1,S,res + 2 ** digit)
    dfs(digit-1,T,res + 2 ** digit)

