    CD = N - AB
    if AB == 1:
        cntAB = 1
    else:
        cntAB = reduce(lambda x, y: x * (y + 1), Counter(prime_factorize(AB)).values(), 1)
    if CD == 1:
        cntCD = 1
    else:
        cntCD = reduce(lambda x, y: x * (y + 1), Counter(prime_factorize(CD)).values(), 1)

    if AB == CD:
        cnt += cntAB * cntCD  # if they are equal, just add the product once
    else:
        cnt += cntAB * cntCD * 2  # otherwise, add both counts for AB and CD

