N, M = map(int, input().split())
graph = [[] for _ in range(N)]
reversed_graph = [0 for _ in range(N)]  # Store the count of incoming edges

for _ in range(M):
    u, v = map(int, input().split())
    graph[u - 1].append(v - 1)
    reversed_graph[v - 1] += 1  # Increase incoming edge count

# Initialize the queue with nodes having no incoming edges
que = []
for i in range(N):
    if reversed_graph[i] == 0:
        heapq.heappush(que, i)

answers = []

while que:
    now = heapq.heappop(que)
    answers.append(now + 1)

    for to in graph[now]:
        reversed_graph[to] -= 1  # Decrease the incoming edge count
        if reversed_graph[to] == 0:
            heapq.heappush(que, to)

if len(answers) == N:
    print(*answers)
else:
    print(-1)

