def max_substring(S, K):
    N = len(S)
    X = [0]*N

    if N==1:
        if (S[0]=='.' and K==1) or (S[0]=='X' and K==0):
            return 1
        else:
            return 0

    if K == 0:
        ans = 0
        for i in range(N):
            if S[i] == 'X':
                j = i
                while j < N and S[j] == 'X':
                    j += 1
                ans = max(ans, j - i)
                i = j - 1  # move i to the last 'X'
        return ans

    for i in range(N):
        if S[i] == '.':
            X[i] += 1

    for i in range(1, N):
        X[i] += X[i - 1]

    X = [0] + X  # Adding a zero at the start
    ans = 0

    for i in range(1, N + 1):
        ok, ng = i, N + 1
        while abs(ok - ng) > 1:
            ic = (ok + ng) // 2
            if X[ic] - X[i - 1] <= K:
                ok = ic
            else:
                ng = ic
        ans = max(ans, ok - i + 1)

    return ans

