def numSrch(c, moves, numSet):
    if c == 1:
        return moves

    if c in numSet and numSet[c] <= moves:
        return float('inf')

    numSet[c] = moves
    min_moves = float('inf')

    if c % a == 0:
        c1 = c // a
        min_moves = min(min_moves, numSrch(c1, moves + 1, numSet))

    if c > 10:
        c2 = str(c)
        c2_rotated = c2[1:] + c2[0]
        if c2_rotated[0] != '0':  # Ensure the rotated number does not lead with '0'
            c2_rotated_int = int(c2_rotated)
            if c2_rotated_int != c:  # Check to avoid infinite loop.
                min_moves = min(min_moves, numSrch(c2_rotated_int, moves + 1, numSet))

    return min_moves

