def solve(args):
    s = args
    n = len(s)
    head = 0
    for i in range(n):
        if s[i] == 'a':
            head += 1
        else:
            break
    tail = 0
    for i in range(n-1, -1, -1):  # Fix the range to iterate in reverse correctly
        if s[i] == 'a':
            tail += 1
        else:
            break

    if head > tail:
        return 'No'

    t = 'a' * (tail - head) + s  # Corrected to ensure that we can consider the correct amount of 'a's

    if t == t[::-1]:  # Check if the entire string t is a palindrome
        return 'Yes'
    else:
        return 'No'

