for j in range(1, M + 1):
    dp[j] = 1  # Start with one way to have a sequence of length 1 for each possible number

for length in range(2, N + 1):
    cum = [0] * (M + 2) # We need one extra for handling boundaries
    for j in range(1, M + 1):
        cum[j] = (cum[j - 1] + dp[j]) % MOD

    ndp = [0] * (M + 1)
    for j in range(1, M + 1):
        if K == 0:
            ndp[j] = (cum[M]) % MOD  # Accumulate all previous counts since any number can go after any other one
        else:
            if j + K <= M:
                ndp[j] = (ndp[j] + cum[M] - cum[j + K - 1]) % MOD
            if j - K >= 1:
                ndp[j] = (ndp[j] + cum[j - K]) % MOD

        # Ensure ndp[j] is non-negative
        ndp[j] = (ndp[j] + MOD) % MOD

    dp = ndp

