        check = set()
        check.add(i)
        Q.append(i)
        while len(Q) > 0:
            a = Q.popleft()
            for j in path[a]:
                if j not in check:
                    Q.append(j)
                    check.add(j)
        if len(check) == N:
            c = 1
            break
    if c:
        ok = m  # If we found a valid configuration, search for smaller m
