        Node hare = node;
        Node tortoise = node;

        while (true) {
if (hare == null || hare.getSuccessor() == null)
                return false;

            tortoise = tortoise.getSuccessor();
            hare = hare.getSuccessor().getSuccessor();

            if (hare == tortoise)
                return true;
        }
    }
}

