import java.util.*;

public class SHUNTING_YARD {
    public static List<Object> shunting_yard(ArrayList<Object> tokens) {
        Map<String, Integer> precedence = new HashMap<>();
        precedence.put("+", 1);
        precedence.put("-", 1);
        precedence.put("*", 2);
        precedence.put("/", 2);

        List<Object> rpntokens = new ArrayList<>();
        ArrayDeque<String> opstack = new ArrayDeque<>();

        for (Object token : tokens) {
            if (token instanceof Integer) {
                rpntokens.add((Integer) token);
            } else if (token instanceof String) {
                String operator = (String) token;
                while (!opstack.isEmpty() && precedence.containsKey(opstack.peek()) &&
                        precedence.get(operator) <= precedence.get(opstack.peek())) {
                    rpntokens.add(opstack.pop());
                }
                opstack.push(operator);
            }
        }

        while (!opstack.isEmpty()) {
            rpntokens.add(opstack.pop());
        }

        return rpntokens;
    }
}

