public class LONGEST_COMMON_SUBSEQUENCE {
    public static String longest_common_subsequence(String a, String b) {
        if (a.isEmpty() || b.isEmpty()) {
            return "";
        } else if (a.charAt(0) == b.charAt(0)) {
return a.charAt(0) + longest_common_subsequence(a.substring(1), b.substring(1));
        } else {
            String fst = longest_common_subsequence(a, b.substring(1));
            String snd = longest_common_subsequence(a.substring(1), b);
            return fst.length() >= snd.length() ? fst : snd;
        }

    }

}

package java_programs;

import java.util.ArrayList;

public class POWERSET {
    public static ArrayList<ArrayList<Object>> powerset(ArrayList<Object> arr) {
        // Base case: if arr is empty, return a list containing the empty set
        if (arr.isEmpty()) {
            ArrayList<ArrayList<Object>> emptySet = new ArrayList<>();
            emptySet.add(new ArrayList<>()); // Add the empty set
            return emptySet;
        }

        // Remove the first element
        Object first = arr.remove(0);
        // Recursively call powerset on the rest of the array
        ArrayList<ArrayList<Object>> restSubsets = powerset(arr);

        // Create output list and add all subsets from rest
        ArrayList<ArrayList<Object>> output = new ArrayList<>(restSubsets);

        // Create new subsets by adding the first element to each of the subsets in restSubsets
        for (ArrayList<Object> subset : restSubsets) {
            ArrayList<Object> newSubset = new ArrayList<>();
            newSubset.add(first); // Add the first element to the new subset
            newSubset.addAll(subset); // Add all elements from the original subset
            output.add(newSubset); // Add the new subset to the output
        }

        // Return the complete list of subsets
        return output;
    }
}
