public class FLATTEN {
    public static Object flatten(Object arr) {
        if (arr instanceof ArrayList) {
            ArrayList<Object> narr = (ArrayList<Object>) arr; // cast to Object
            ArrayList<Object> result = new ArrayList<>();
            recurse(arr, result);
            return result;
        } else {
            return arr; // base case
        }
    }

    private static void recurse(Object arr, ArrayList<Object> result) {
        for (Object x : (ArrayList<?>)((Collection<?>)arr)) {
            if (x instanceof ArrayList) {
                recurse(x, result);
            } else {
                result.add(x);
            }
        }
    }
}

