public class SHUNTING_YARD {
    /**
     * Implement the Shunting Yard algorithm
     *
     * @param tokens
     * @return
     */
    public static List<String> shunting_yard(List tokens) {
        Map<String, Integer> precedence = new HashMap<String, Integer>();
        precedence.put("+", 1);
        precedence.put("-", 1);
        precedence.put("*", 2);
        precedence.put("/", 2);

        List<String> output = new ArrayList<>();
        Stack<String> operatorStack = new Stack<>();

        for (Object token : tokens) {
            if (token instanceof Integer) {
                output.add(String.valueOf((Integer) token));
            } else {
                String operator = (String) token;
                while (!operatorStack.isEmpty() && precedence.containsKey(operator) && precedence.get(operator) <= precedence.get(operatorStack.peek())) {
                    output.add(operatorStack.pop());
                }
                operatorStack.push(operator);
            }
        }

        // Pop remaining operators from stack
        while (!operatorStack.isEmpty()) {
            output.add(operatorStack.pop());
        }

        return output;
    }
}

