public class REVERSE_LINKED_LIST {
    public static Node reverse_linked_list(Node head) {
        Node prevnode = null;
        Node currnode = head;
        while (currnode != null) {
            Node nextnode = currnode.getSuccessor();
            currnode.setSuccessor(prevnode);
            prevnode = currnode;
            currnode = nextnode;
        }
        return prevnode;
    }
}

