import java.util.*;

public class SHUNTING_YARD {
    public static List<Object> shunting_yard(ArrayList<Object> tokens) {
        Map<String, Integer> precedence = new HashMap<String, Integer>();
        precedence.put("+", 1);
        precedence.put("-", 1);
        precedence.put("*", 2);
        precedence.put("/", 2);

        ArrayList<Object> rpnTokens = new ArrayList<>();
        Deque<Object> opStack = new ArrayDeque<>();

        for (Object token : tokens) {
            if (Integer.class.isInstance(token)) {
                rpnTokens.add(token);
            } else {
                String operator = (String) token;
                while (!opStack.isEmpty() && !operator.equals("(") && precedence.get(opStack.peek()) >= precedence.get(operator)) {
                    rpnTokens.add(opStack.pop());
                }
                opStack.push(operator);
            }
        }

        while (!opStack.isEmpty()) {
            rpnTokens.add(opStack.pop());
        }

        return rpnTokens;
    }
}

