public class REVERSE_LINKED_LIST {
    // Assuming that Node class has a getter and setter for successor method
    public static Node reverse_linked_list(Node head) {
        Node prevnode = null;
        Node currnode = head;
        while (currnode != null) {
            Node nextnode = currnode.getSuccessor();
            currnode.setSuccessor(prevnode);
            prevnode = currnode;
            currnode = nextnode;
        }
        head = prevnode; // Update the head with the new reversed list's head
        return head;
    }
}

