public class DEPTH_FIRST_SEARCH {
    public static boolean depth_first_search(Node startnode, Node goalnode) {
        Set<Node> nodesvisited = new HashSet<>();
        return depth_first_search(startnode, goalnode, nodesvisited);
    }

    private static boolean depth_first_search(Node node, Node goalnode, Set<Node> nodesvisited) {
        if (nodesvisited.contains(node)) {
            return false;
        }
        nodesvisited.add(node);

        if (node == goalnode) {
            return true;
        }

        for (Node successor : node.getSuccessors()) {
            if (depth_first_search(successor, goalnode, nodesvisited)) {
                return true;
            }
        }

        return false;
    }
}

